text.validate:
for x in this.rows:				
  if x.val:				
    err1 = 0				
    for y in this.rows:				
      if y.index != x.index and y.val and (x.val).strip() == (y.val).strip():				
        err1 = 1				
    if err1 == 1:				
      error(res.DupSame, row=x)				
  if not x.val:				
    err2 = 0				
    for y in this.rows:				
      if y.index gt x.index and y.val:				
        err2 = 1				
    if err2 == 1:				
      error(res.DupOrder, row=x)
