number.exec:
if Location.ZZ999:
	IdNumber = int(record)
else:
	IdNumber = (Phase.val+1)*10000+int(record)

Resp.val = IdNumber

number.validate:
if P_QuantityPurchased.sum == 0:	error(res.ShoppingError)
for row in P_QuantityPurchased.rows:
        if P_QuantityPurchased.rows[row.index].c3.ival gt 0 and not(P_PickUp_BackOfPack.rows[row.index].c1):    error("RESPONDENT MUST PICK UP A PRODUCT IN ORDER TO PURCHASE IT")

checkbox.exec:
if P_QuantityPurchased.any:
        for eachRow in P_PickUp_BackOfPack.rows:
                if P_PickUp_BackOfPack.rows[eachRow.index].c1:
                        P_PickUpTotal.rows[eachRow.index].val = 1
                else:
                        P_PickUpTotal.rows[eachRow.index].val = 0

if P_QuantityPurchased.any:
        for eachRow in P_PickUp_BackOfPack.rows:
                if P_PickUp_BackOfPack.rows[eachRow.index].c2:
                        P_BackPanelTotal.rows[eachRow.index].val = 1
                else:
                        P_BackPanelTotal.rows[eachRow.index].val = 0

if P_QuantityPurchased.any:
	for eachRow in P_QuantityPurchased.rows:
  		if eachRow.ival gt 0:
			P_Shopping.rows[eachRow.index].val = 1 
  		else:
  			P_Shopping.rows[eachRow.index].val = 0

text.validate:
correct = 0
ans = FindLetter.val.upper() + str(FindNumber.val)
for i, row in enumerate(P_Shopping.rows[::-1]):
	if P_Shopping[row].text[:len(ans)] == ans and P_Shopping[row].text[len(ans)].isdigit()==False:
		correct = 1
if correct == 0:	error("THIS LETTER/NUMBER COMBINATION DOES NOT EXIST")

radio.exec:
ans = FindLetter.val.upper() + str(FindNumber.val)
for i, row in enumerate(FindProduct.rows[::-1]):
	if FindProduct[row].text[:len(ans)] == ans and FindProduct[row].text[len(ans)].isdigit()==False:
  		FindProduct.val = len(FindProduct.rows)-1-i

if (FindLetter.val.lower() == "d" and FindNumber.val == "2"):
  vFindCorrect.val = 0
else:
  vFindCorrect.val = 1

if not(gv.isSST()) and p.endTime - p.startTime le 16 and (F_LineCommunication.c1.all or F_LineCommunication.c2.all or F_LineCommunication.c3.all or F_LineCommunication.c4.all or F_LineCommunication.c5.all):
  Straightliner2.val = 0
else:
  Straightliner2.val = 1
